let user = {name:"9SAI",email:"9sai@gmail.com",password:"9sai1234"}

// converting user sensitive data using bcrypt js package

// USING IMPORT
let bcrypt = require("bcryptjs")

let salt = bcrypt.genSaltSync(10);
let new_Password = bcrypt.hashSync(user.password, salt)
console.log(user.password)
console.log(new_Password)

// SPREAD OPERATOR
let new_User = {...user,password:new_Password}
console.log(user)
console.log(new_User)

let flag = bcrypt.compareSync("9sai1234",new_User.password)

// if else
if (flag == true) {
    console.log("LOGIN SUCCESS")
}
else {
    console.log("LOGIN FAILURE")
}