let user = {id:101,name:"9SAI",password:"1234"}
const bcrypt = require ("bcryptjs")
let salt = bcrypt.genSaltSync(1)
let new_Password = bcrypt.hashSync(user.password,salt)
console.log(salt)
console.log(new_Password)
let new_User = {...user,password:new_Password}
console.log(user)
console.log(new_User)
let flag = bcrypt.compareSync("1234",new_User.password)
if (flag == true) {
    console.log("TEST OKAY")
}
else {
    console.log("TEST NOT OKAY")
}